CREATE TABLE category (id BIGINT AUTO_INCREMENT, group_type VARCHAR(10), name_hu VARCHAR(100), name_en VARCHAR(100), name_ru VARCHAR(100), name_pl VARCHAR(100), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_hungarian_ci ENGINE = MYISAM;
CREATE TABLE message (id BIGINT AUTO_INCREMENT, user_id BIGINT, theme TINYINT, mesage text, INDEX user_id_idx (user_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_hungarian_ci ENGINE = MYISAM;
CREATE TABLE news (id BIGINT AUTO_INCREMENT, create_date DATETIME NOT NULL, title_hu VARCHAR(100), title_en VARCHAR(100), title_ru VARCHAR(100), title_pl VARCHAR(100), news_hu text, news_en text, news_ru text, news_pl text, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_hungarian_ci ENGINE = MYISAM;
CREATE TABLE product (id BIGINT AUTO_INCREMENT, category_id BIGINT, name_hu VARCHAR(100), name_en VARCHAR(100), name_ru VARCHAR(100), name_pl VARCHAR(100), description_hu text, description_en text, description_ru text, description_pl text, guide_hu VARCHAR(100), guide_en VARCHAR(100), guide_ru VARCHAR(100), guide_pl VARCHAR(100), INDEX category_id_idx (category_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_hungarian_ci ENGINE = MYISAM;
CREATE TABLE user (id BIGINT AUTO_INCREMENT, email VARCHAR(100), name VARCHAR(100), tel VARCHAR(20), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_hungarian_ci ENGINE = MYISAM;
ALTER TABLE message ADD CONSTRAINT message_user_id_user_id FOREIGN KEY (user_id) REFERENCES user(id) ON DELETE CASCADE;
ALTER TABLE product ADD CONSTRAINT product_category_id_category_id FOREIGN KEY (category_id) REFERENCES category(id) ON DELETE CASCADE;
